import React from 'react';


class Board extends React.Component {

  // the plus one for index is for users
  // who arent aware of zero index
  render() {
    let className = "board";
    if (this.props.selected) {
      className += " selected";   
    }
    return (
      <div className={className}>
        {this.props.index + 1}
      </div>
    );
  }
}

// index and selected arent important words unlike key
// which is used to differentiate different boards
// when your using an array with the same elements
// you have to give a key 
class BoardSwitcher extends React.Component {

  state = {
    currSelected: 0,
  }

  handleToggle(event){
    if (this.state.currSelected < this.props.numBoards-1)
        this.setState({currSelected:  this.state.currentSelector + 1})
        else 
          this.setState({currSelected: 0})
  } 

  render() {
    let boards = [];
    for (let ii = 0; ii < this.props.numBoards; ii++) {
      let isSelected = ii === 0;
      boards.push(
        <Board index={ii} selected={isSelected} key={ii} />
      );
    }

    return (
      <div>
        <div className="boards">{boards}</div>
        <button onClick={ (event) => this.handleTog(event) }>Toggle</button>
      </div>
    );
  }
}

export default BoardSwitcher;
